sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

		//query your values
    var loc_codes = new GlideRecord('x_snc_dcph_databas_location_codes');
    //loc_codes.addQuery('loc_code','NOT','');
    loc_codes.query();

    while (loc_codes.next()) {
		//push your values to the data object
        var locObj = {};
        locObj.loc_code = loc_codes.loc_cd.toString();
				locObj.loc_long_desc = loc_codes.loc_long_desc.toString();
				locObj.addr_line_1 = loc_codes.addr_line_1.toString();
				locObj.addr_line_2 = loc_codes.addr_line_2.toString();
				locObj.city_name = loc_codes.city_name.toString();
				locObj.cnty_cd = loc_codes.cnty_cd.toString();
				locObj.st_cd = loc_codes.st_cd.toString();
				locObj.zip_prefix = loc_codes.zip_prefix.toString();
        data.loc_codes.push(locObj);

    }

})();
